/*Table structure for table `user` */

DROP TABLE IF EXISTS `user`;

CREATE TABLE `user` (
  `id` int(4) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `author` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '作者',
  `name` varchar(10) CHARACTER SET utf8 NOT NULL COMMENT '用户名',
  `password` varchar(50) CHARACTER SET utf8 NOT NULL COMMENT '密码',
  `description` varchar(300) CHARACTER SET utf8 NOT NULL COMMENT '描述',
  `title` varchar(20) CHARACTER SET utf8 NOT NULL COMMENT '标题',
  `background` varbinary(100) NOT NULL COMMENT '背景颜色',
  `notice` varchar(999) CHARACTER SET utf8 DEFAULT NULL COMMENT '公告',
  `subtitle` varchar(100) CHARACTER SET utf8 DEFAULT NULL COMMENT '副标题',
  `running_time` datetime NOT NULL COMMENT '已运行时间',
  `favicon` varchar(500) CHARACTER SET utf8 NOT NULL COMMENT '博客图标',
  `avatar` varchar(500) CHARACTER SET utf8 NOT NULL COMMENT '头像',
  `email` varchar(50) CHARACTER SET utf8 DEFAULT NULL COMMENT '邮箱',
  `icp` varchar(20) CHARACTER SET utf8 DEFAULT NULL COMMENT 'ICP备案',
  `about` mediumtext CHARACTER SET utf8 NOT NULL COMMENT '关于我',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

/*Data for the table `user` */

insert  into `user`(`id`,`author`,`name`,`password`,`description`,`title`,`background`,`notice`,`subtitle`,`running_time`,`favicon`,`avatar`,`email`,`icp`,`about`) values (1,'Lete乐特','admin','96e79218965eb72c92a549dd5a330112','我相信我可以，但我一直在路上，所以我有无限的可能！！','Lete乐特博客','url(https://cdn.jsdelivr.net/gh/lete114/CDN2/img/wei_er_li_te/3.jpg)','目前处于测试阶段，后台\r\n用户名：<span style=\"color: red;\">admin</span>\r\n密码：<span style=\"color: red;\">111111</span>为防止他人篡改密码等其他信息，部分功能将被关闭，包括评论邮件提醒','人生只有一次，大胆的生活，怎么舒服怎么来！！,我相信我可以，但我一直在路上，所以我有无限的可能！！','2020-04-15 00:00:00','https://www.lete114.top/img/favicon.ico','https://www.lete114.top/img/avatar.png','lete@lete114.top','','## 关于我\n\n昵称: Lete乐特\n性别：男♂\n生活：00年，喜欢单独一个人，安静，注意生活中的细节，大山里长大\n爱好: IT，Minecraft，动漫，摄影，西方古文化，西方中世纪\n\n## 关于博客\n\n[Hexo](https://hexo.io/zh-cn) 框架 + [Butterfly](https://github.com/jerryc127/hexo-theme-butterfly) 主题\n[Github](https://github.com/) 存储 + [Jsdeliv&nbsp;CDN](https://www.jsdelivr.com/) 加速\n部署：[Github](https://github.com/) + [Vercel](https://vercel.com/) + [Coding](https://coding.net/)\n博客中的部分图片源于网络，侵删\n本博客文章采用 [CC BY-NC-SA 4.0](https://creativecommons.org/licenses/by-nc-sa/4.0/) 协议，转载请注明出处\n\n## 技能掌握\nJava, C# , MySQL , SQLServer , Servlet \nJSP , HTML , CSS , JavaScrip , Ajax\nMyBatis\n\n## 在学习\nSpring\nSpringMVC\nSpringBoot\n\n\n<i style=\"color:#FF6A6A;animation: announ_animation 0.8s linear infinite;\" class=\"fa fa-heartbeat\"></i>&nbsp;感谢你花时间阅读我的博客，欢迎与我沟通交流。祝好！&nbsp;<i style=\"color:#FF6A6A;animation: announ_animation 0.8s linear infinite;\" class=\"fa fa-heartbeat\"></i>');

