mapper.select:
select * from comment
where 1=1

    AND blogId = #{blogId}


    AND commentStatus = #{commentStatus}

order by commentId desc

    limit #{start},#{limit}

select count(*) from comment
where 1=1

    AND blogId = #{blogId}


    AND commentStatus = #{commentStatus}

mapper.delete:
delete from comment
where commentId in

    #{id}

mapper.update:
update comment
set commentStatus=1 where commentId in

    #{id}
